INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2087382', '12', '60606060', '<p>简述“海上画派”。</p>', '', '2013年上半年教师资格证考试《初中美术》题第27题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2087382';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2087382', '2087382', '12', '', '1', '<p>（1）海上画派，国画画派之一，又称“海派”，或“沪派”，一般指的是发生于十九世纪中至二十世纪初期，一群画家活跃于上海地区，并从事绘画创作的人物。</p><p>（2）“海派”善于把诗书画一体的文人画传统与民间美术传统结合起来，又从古代刚健雄强的金石艺术中吸取营养，描写民间喜闻乐见的题材，将明清以来大写意水墨画技艺和强烈的色彩相结合，形成雅俗共赏的新风貌。</p><p>（3）主要代表人物有：虚谷、任伯年、吴昌硕和蒲华等。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2087382';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2087382', ',2087382', '2087382', '<p>（1）海上画派，国画画派之一，又称“海派”，或“沪派”，一般指的是发生于十九世纪中至二十世纪初期，一群画家活跃于上海地区，并从事绘画创作的人物。</p><p>（2）“海派”善于把诗书画一体的文人画传统与民间美术传统结合起来，又从古代刚健雄强的金石艺术中吸取营养，描写民间喜闻乐见的题材，将明清以来大写意水墨画技艺和强烈的色彩相结合，形成雅俗共赏的新风貌。</p><p>（3）主要代表人物有：虚谷、任伯年、吴昌硕和蒲华等。</p>');
