INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2087398', '12', '60606060', '<p>简述美术合作学习的分组原则。</p>', '', '2013年上半年教师资格证考试《初中美术》题第29题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2087398';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2087398', '2087398', '12', '', '1', '<p>合作学习，首先需要考虑就是学生分组的问题，这也是合作学习是否取得成功的前提。良好的分组合作可以使的同学之间相互学习、共同进步。具体分组原则有以下几方面：</p><p>（1）同组异质：将不同层次的学生搭配分组，这样有利于同学之间互帮互助、共同进步。结合美术学科特点，分组可以从以下几个因素考虑：美术基础、性格差异、性别、班干部、学习态度等。</p><p>（2）异组同质：保持组与组之间的相对均衡性，不仅有利于组际间的交流和竞争，还有利于对各组学习活动的评价。所以在分组后要使的各个美术活动小组的综合实力相对统一。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2087398';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2087398', ',2087398', '2087398', '<p>合作学习，首先需要考虑就是学生分组的问题，这也是合作学习是否取得成功的前提。良好的分组合作可以使的同学之间相互学习、共同进步。具体分组原则有以下几方面：</p><p>（1）同组异质：将不同层次的学生搭配分组，这样有利于同学之间互帮互助、共同进步。结合美术学科特点，分组可以从以下几个因素考虑：美术基础、性格差异、性别、班干部、学习态度等。</p><p>（2）异组同质：保持组与组之间的相对均衡性，不仅有利于组际间的交流和竞争，还有利于对各组学习活动的评价。所以在分组后要使的各个美术活动小组的综合实力相对统一。</p>');
