INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2087418', '12', '60606060', '<p>一节美术欣赏课的教学要点有哪些？</p>', '', '2014年上半年教师资格证考试《初中美术》题第29题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2087418';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2087418', '2087418', '12', '', '1', '<p>1、“欣赏·评述”学习领域是指学生通过对自然美、美术作品和美术现象等进行观察、描述和分析，逐步形成审美趣味和美术欣赏能力的学习领域。</p><p>2、欣赏·评述是学生终身的事情，因此，欣赏课的教学要点应该放在鉴赏能力和方法的培养上。</p><p>3、学生除了通过欣赏获得审美愉悦之外，还应认知作品的思想内涵、形式与风格特征，相关的历史与社会背景，以及作者的思想、情感和创造性的劳动，并用语言、文字、动作等多种方式表达自己的感受与认识。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2087418';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2087418', ',2087418', '2087418', '<p>1、“欣赏·评述”学习领域是指学生通过对自然美、美术作品和美术现象等进行观察、描述和分析，逐步形成审美趣味和美术欣赏能力的学习领域。</p><p>2、欣赏·评述是学生终身的事情，因此，欣赏课的教学要点应该放在鉴赏能力和方法的培养上。</p><p>3、学生除了通过欣赏获得审美愉悦之外，还应认知作品的思想内涵、形式与风格特征，相关的历史与社会背景，以及作者的思想、情感和创造性的劳动，并用语言、文字、动作等多种方式表达自己的感受与认识。</p>');
