INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2089326', '2089326', '12', '', '1', '<p>学习迁移也称训练迁移，是指一种学习对另一种学习的影响，或习得的经验对完成其他活动的影响，如通常所说的“举一反三”“触类旁通”。</p>', '0');
