INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2089340', '2089340', '12', '', '1', '<p>教学任务有：</p><p>（1）引导学生掌握科学文化基础知识和基本技能；</p><p>（2）发展学生智力，培养学生的创造力和实践能力；</p><p>（3）发展学生体力，提高学生的健康水平；</p><p>（4）培养小学生高尚的审美情趣，养成良好的思想品德，形成科学的世界观和良好的个性心理品质。</p>', '0');
