INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2089542', '12', '70707070', '<p>请简述组织教学过程中需要设计的程序性事项有哪些？并举实例说明其中的任一程序性事项。</p>', '', '2014年江苏省连云港市海州区教师招聘考试题第53题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2089542';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2089542', '2089542', '12', '', '1', '<p>（1）教学是有一定程序结构的。在教学程序中，学生的学习随事先设计的教学情境而进行，教师安排的程序性事项就是教学事项。加涅指出，在教学中，要依次完成以下九大教学事项。</p><p>①引起学生注意。</p><p>②提示教学目标。</p><p>③唤起先前经验。</p><p>④呈现教学内容。</p><p>⑤提供学习指导。</p><p>⑥展现学习行为。</p><p>⑦适时给予反馈。</p><p>⑧评定学习结果。</p><p>⑨加强记忆与学习迁移。</p><p>（2）举例：以引起学生注意教学事项为例。</p><p>引起学生注意是教学过程中的首要事件。教师可以通过三种方式来引起学生的注意：第一，激发求知欲，即由教师提出问题，学生们为了知道问题的答案，就会集中注意于教师的讲解以及其他相关的教学活动。第二，变化教学情境，即通过教学媒体，提高教学的直观性和形象性，促进学生的感知和思维活动。第三，配合学生经验，即从学生最关心的问题入手，结合日常生活经验，然后转到所教主题之上。</p><p>例子：地理课，老师讲自然灾害，为了引起学生的注意，增强主观感受，可以播放关于九寨沟地震或者汶川地震的视频，增强主观感受，激发学生注意力和思考。</p>', '9');
DELETE FROM edu_course_exercise_comment WHERE id='2089542';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2089542', ',2089542', '2089542', '<p>（1）教学是有一定程序结构的。在教学程序中，学生的学习随事先设计的教学情境而进行，教师安排的程序性事项就是教学事项。加涅指出，在教学中，要依次完成以下九大教学事项。</p><p>①引起学生注意。</p><p>②提示教学目标。</p><p>③唤起先前经验。</p><p>④呈现教学内容。</p><p>⑤提供学习指导。</p><p>⑥展现学习行为。</p><p>⑦适时给予反馈。</p><p>⑧评定学习结果。</p><p>⑨加强记忆与学习迁移。</p><p>（2）举例：以引起学生注意教学事项为例。</p><p>引起学生注意是教学过程中的首要事件。教师可以通过三种方式来引起学生的注意：第一，激发求知欲，即由教师提出问题，学生们为了知道问题的答案，就会集中注意于教师的讲解以及其他相关的教学活动。第二，变化教学情境，即通过教学媒体，提高教学的直观性和形象性，促进学生的感知和思维活动。第三，配合学生经验，即从学生最关心的问题入手，结合日常生活经验，然后转到所教主题之上。</p><p>例子：地理课，老师讲自然灾害，为了引起学生的注意，增强主观感受，可以播放关于九寨沟地震或者汶川地震的视频，增强主观感受，激发学生注意力和思考。</p>');
