INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2089720', '2089720', '12', '', '1', '<p>性格是指人的较稳定的态度与习惯化了的行为方式相结合而形成的人格特征。性格是个性的核心，具有独特性，在个性心理特征中具有核心意义。它是一个人心理面貌本质属性的独特结合，是人与人相互区别的主要方面。</p>', '0');
