INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2117106', '2117106', '12', '', '1', '<p>班主任的常规工作有以下几方面。</p><p>（1）了解和研究学生。</p><p>（2）组织和培养班集体。</p><p>（3）建立学生成长档案。</p><p>（4）组织班会活动和课外活动。</p><p>（5）协调各种教育影响。</p><p>（6）操行评定。</p><p>（7）写好班主任工作计划与总结。</p>', '0');
