INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2135546', '12', '70707070', '<p>如何培养学生的创造性思维？</p>', '', '2014年安徽省教师招聘考试《教育综合知识》（中学）题第59题', '1', '0');
