INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2135546', '2135546', '12', '', '1', '<p>创造性思维是以新颖、独特的方式来解决问题的思维方式。培养学生创造性思维的常用方法有以下几种：</p><p>（1）发散思维训练。</p><p>（2）推测与假设训练。</p><p>（3）自我设计训练。</p><p>（4）头脑风暴训练。</p>', '0');
