INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2135718', '12', '70707070', '<p>简述班级管理的内容。</p>', '', '2015年四川省特岗教师招聘考试《教育综合知识》题第36题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2135718';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2135718', '2135718', '12', '', '1', '<p>班级管理属于一种微观、具体的学校管理层次。其主要管理工作范围是对组成班级的学生的管理。因此，围绕育人的教育目标，现代中小学校班级管理的基本内容包括以下方面：</p><p>（1）班级教学管理；</p><p>（2）班级德育管理；</p><p>（3）班级体育卫生和课外活动的管理；</p><p>（4）班级生活指导。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2135718';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2135718', ',2135718', '2135718', '<p>班级管理属于一种微观、具体的学校管理层次。其主要管理工作范围是对组成班级的学生的管理。因此，围绕育人的教育目标，现代中小学校班级管理的基本内容包括以下方面：</p><p>（1）班级教学管理；</p><p>（2）班级德育管理；</p><p>（3）班级体育卫生和课外活动的管理；</p><p>（4）班级生活指导。</p>');
