INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2135734', '12', '70707070', '<p>简述在教师的权利与义务实现过程中道德因素的影响。</p>', '', '2015年四川省特岗教师招聘考试《教育综合知识》题第39题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2135734';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2135734', '2135734', '12', '', '1', '<p>在教师的权利与义务实现过程中，道德因素的影响有：</p><p>（1）教师在享有权利的选择上受到道德价值观念的支配；</p><p>（2）教师在履行教育义务的主动性上受道德水平的制约；</p><p>（3）在教师的法律权利、义务实现保障措施不完备的情况下，教师职业道德能起到弥补作用。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2135734';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2135734', ',2135734', '2135734', '<p>在教师的权利与义务实现过程中，道德因素的影响有：</p><p>（1）教师在享有权利的选择上受到道德价值观念的支配；</p><p>（2）教师在履行教育义务的主动性上受道德水平的制约；</p><p>（3）在教师的法律权利、义务实现保障措施不完备的情况下，教师职业道德能起到弥补作用。</p>');
