INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2160912', '12', '91919191', '<p>简述教学日志（teaching journal）的含义和三个作用，列出教师撰写教学日志的三点注意事项。</p>', '', '2017年下半年教师资格证考试《高中英语》题第31题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2160912';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2160912', '2160912', '12', '教学日志（teaching journal）的含义', '1', '<p>（1）含义：教学日志是教师积极主动地对自己的教学活动中具有反思和研究价值的经验所进行的持续而真实的记录和描写，并在此基础上对其进行批判的理解和认识，从而不断更新观念、增长技能，促进自身专业发展的一种手段和方法，也是一种对教师个人认识与思维的研究。</p>', '5');
DELETE FROM edu_course_exercise_qa WHERE id='2160912218';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2160912218', '2160912', '12', '教学日志的三个作用', '1', '<p>（2）作用：</p><p>①检验教学嘉定，改善教学行为。</p><p>②促进教师交流，利于成果分享。</p><p>③促进深入思考，形成教学思想。</p><p>④促进字数学习，提高理论修养。</p><p>⑤体验发现过程，提高工作乐趣。</p><p>（只需要选择其中的三个方面内进行作答即可。）</p>', '9');
DELETE FROM edu_course_exercise_qa WHERE id='2160912405';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2160912405', '2160912', '12', '教师撰写教学日志的三点注意事项', '1', '<p>注意事项：</p><p>①要注意亲历性。亲历性要求教学日志所描述的时间必须是自己亲身经历的，而不是来自某一文献或他人的传说，以保证教学日志的独特性与原创性。</p><p>②要注意叙事性。教学日志是教育叙事研究的一种范式，它记述的是一个教学时间从发生、发展到对结果进行反思的过程。因此教学日志要以教学事件为载体。</p><p>③要注意启示性。教学日志记录教学时间不能知识关注时间本身，而应注意教学时间所承载的思想。</p><p>④要注意典型性。教学日志记录的应当是对自己和他人有思想启迪的事件。教师应当对各种教学时间进行精心选择和甄别，选择有价值的事件进行记录。</p><p>⑤要注意即时性。课后立即写教学日志是较理想的，因为现实的教学活动还鲜活地存在大脑里，很多当时认识、感受和情景还没有遗忘。</p><p>（只需要选择其中三个方面进行作答即可。）</p>', '6');
DELETE FROM edu_course_exercise_comment WHERE id='2160912';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2160912', ',2160912', '2160912', '<p>本文考察的是教学实施概述中的教学反馈。教学日志是教师对自己的教学活动进行反思的一种有效手段。因此需要考生回忆教学日志的含义及三个作用，并列出写教学日志的注意事项。</p>');
