INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2168318', '12', '22222222', '<p>贯穿普通高中《思想政治》课程始终的基本理念是什么？</p>', '', '2014年浙江省教师招聘考试学科专业知识（中学政治）第31题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2168318';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2168318', '2168318', '12', '', '1', '<p>（1）坚持马克思主义基本常识教育与顺应时代发展要求相统一，体现当今世界和中国发展的时代特征，显示马克思主义科学理论的强大力量。<br></p><p>（2）注重政治方向的引导与学生成长阶段的特点相结合，在尊重学生个性差异和各种生活关切的同时，力求采取释疑解惑、循循善诱的方式，帮助他们认同正确的价值标准，把握正确的政治方向。</p><p>（3）构建以生活为基础、以学科知识为支撑的课程模块。本课程要立足于学生现实的生活经验，着眼于学生未来的发展需求，把理论观点的阐述寓于生活主题之中。</p><p>（4）强调课程实施的实践性和开放性。本课程要引领学生在认识社会、适应社会、参与社会的实践活动中，感受经济、政治、文化各个生活领域应用知识的价值和理性思考的意义。</p>', '7');
DELETE FROM edu_course_exercise_comment WHERE id='2168318';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2168318', ',2168318', '2168318', '<p>（1）坚持马克思主义基本常识教育与顺应时代发展要求相统一，体现当今世界和中国发展的时代特征，显示马克思主义科学理论的强大力量。<br></p><p>（2）注重政治方向的引导与学生成长阶段的特点相结合，在尊重学生个性差异和各种生活关切的同时，力求采取释疑解惑、循循善诱的方式，帮助他们认同正确的价值标准，把握正确的政治方向。</p><p>（3）构建以生活为基础、以学科知识为支撑的课程模块。本课程要立足于学生现实的生活经验，着眼于学生未来的发展需求，把理论观点的阐述寓于生活主题之中。</p><p>（4）强调课程实施的实践性和开放性。本课程要引领学生在认识社会、适应社会、参与社会的实践活动中，感受经济、政治、文化各个生活领域应用知识的价值和理性思考的意义。</p>');
