INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2168884', '12', '70707070', '<p>班级授课制的优点是什么？</p>', '', '2018年1月7日山西省长治市教师招聘考试《教育基础知识》（中学）第82题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2168884';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2168884', '2168884', '12', '', '1', '<p>班级授课制的优点是：</p><p>（1）有严格的制度保证教学的正常开展，达到一定质量。</p><p>（2）有利于大面积培养人才。</p><p>（3）能够充分发挥教师的主导作用。</p><p>（4）有利于发挥集体教育的作用。</p><p>（5）有利于学生获得系统的科学知识。</p><p>（6）有利于进行教学管理和教学检查。</p>', '0');
