INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2169721', '12', '61616161', '<p>简述教材编写的基本要求。</p>', '', '2018年上半年教师资格证考试《教育知识与能力》（中学）题第26题', '1', '0');
