INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2169722', '2169722', '12', '', '1', '<p>作为班主任，在思想、知识、管理、能力等方面应具备的水平和能力如下。</p><p>（1）高尚的思想品德素质。</p><p>（2）广博的知识与才能。</p><p>（3）较强的组织管理能力。</p><p>（4）良好的沟通与交往能力。</p>', '10.0');
