INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2171630', '12', '80808080', '<p>任选一题作文（不透露个人信息，卷面整洁，书写规范，不少于800字）</p><p>题目一：教师的品格与信仰。</p><p>题目二：评述长篇小说陈忠实《白鹿原》或加西亚 · 马尔克斯《百年孤独》。</p><p>要求：自选角度，自拟题目，集中一点，写深写透。</p>', '', '2016年安徽省教师招聘考试《中学语文》题第23题', '1', '0');
