INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2172022', '12', '32323232', '<p>中学美术课程类型有哪些？</p>', '', '2017年江西省教师招聘考试学科专业知识（初中美术）第62题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2172022';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2172022', '2172022', '12', '', '1', '<p>在中学美术教学过程中，美术教师根据不同的教学任务而确定的上课的类型，即为课型。课型分为新授课、复习课、实验课、实习课、检查课、测验课、综合课和活动课等。</p><p>（1）新授课是以讲解新知识为目的的课。</p><p>（2）复习课是以复习巩固所学知识为目的的课。</p><p>（3）实验课、实习课是以培养学生技能、技巧为目的的课。</p><p>（4）检查课、测验课是以检查学生掌握知识和技能程度为目的的课。</p><p>（5）综合课、活动课是将讲授、复习巩固、检查提问、作业练习等活动交叉进行的课。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2172022';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2172022', ',2172022', '2172022', '<p>在中学美术教学过程中，美术教师根据不同的教学任务而确定的上课的类型，即为课型。课型分为新授课、复习课、实验课、实习课、检查课、测验课、综合课和活动课等。</p><p>（1）新授课是以讲解新知识为目的的课。</p><p>（2）复习课是以复习巩固所学知识为目的的课。</p><p>（3）实验课、实习课是以培养学生技能、技巧为目的的课。</p><p>（4）检查课、测验课是以检查学生掌握知识和技能程度为目的的课。</p><p>（5）综合课、活动课是将讲授、复习巩固、检查提问、作业练习等活动交叉进行的课。</p>');
