INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2174060', '12', '32323232', '<p>中国山水画与西方风景画有什么区别？</p>', '', '2016年山东省临沂市教师招聘考试学科专业知识（美术）第32题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2174060';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2174060', '2174060', '12', '', '1', '<p>（1）工具材料不同。因为中国山水画使用的是最富有弹力的毛笔和对笔触、水分变化反应最为灵敏的宣纸，所以形成了中国山水画笔墨不易塑形，笔触变化无穷的特点。西方风景画使用的是与中国山水画作画工具完全不同的油画笔、油性颜料与画布。</p><p>（2）观察方法和表现方法不同。西方风景画大多以焦点透视的方法对照实物如实描绘，中国山水画则讲究“搜尽奇峰打草稿”，对画面目识心记，构图采用散点透视，形成独幅或长卷。</p><p>（3）中国山水画强调笔墨意境的追求，西方风景画多表现现实的景物。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2174060';
