INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2174571', '12', '32323232', '<p>连线题。</p><p>后印象派            列宾             《自由引导人民》 </p><p>巡回展览画派    德拉克洛瓦  《倒牛奶的女仆》  </p><p>浪漫主义            塞尚             《伏尔加河上的纤夫》  </p><p>野兽派               维米尔          《舞蹈》  </p><p>荷兰小画派        马蒂斯         《圣维克多山》  </p>', '', '2016年安徽省教师招聘考试学科专业知识（中学美术）第31题', '1', '0');
