INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2174571', '2174571', '12', '', '0', '<p><img width="700px" height="178px" src="https://fb.fbstatic.cn/api/tarzan/images/1626b85e7ddd88a.png?width=700" /></p>', '0');
