INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2179343', '12', '80808080', '<p>《义务教育语文课程标准（2011年版）》指出：“阅读是学生的个性化行为。”请你谈谈对这句话的理解。</p>', '', '2017年江西省教师招聘考试《初中语文》题第57题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2179343';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2179343', '2179343', '12', '', '1', '<p>《义务教育语文课程标准（2011年版）》指出：“阅读是学生的个性化行为，不应以教师的分析来代替学生的阅读实践，应让学生在主动积极的思维和情感活动中，加深理解和体验，有所感悟和思考，受到情感熏陶，获得思想启迪，享受审美乐趣。”学生个性化阅读，是学生从书面语言中获取信息，并与非认知因素互为影响的活动，存在着个体差异。它是学生借助文本与作者对话的心理过程，具有鲜明的个性特征，它是教师引导学生探究性感受、理解、赏析、评价文本的过程。每个学生的生活积淀、文化底蕴、审美情趣千差万别，因此阅读是一种个性化的行为。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2179343';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2179343', ',2179343', '2179343', '<p>《义务教育语文课程标准（2011年版）》指出：“阅读是学生的个性化行为，不应以教师的分析来代替学生的阅读实践，应让学生在主动积极的思维和情感活动中，加深理解和体验，有所感悟和思考，受到情感熏陶，获得思想启迪，享受审美乐趣。”学生个性化阅读，是学生从书面语言中获取信息，并与非认知因素互为影响的活动，存在着个体差异。它是学生借助文本与作者对话的心理过程，具有鲜明的个性特征，它是教师引导学生探究性感受、理解、赏析、评价文本的过程。每个学生的生活积淀、文化底蕴、审美情趣千差万别，因此阅读是一种个性化的行为。</p>');
