INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2180269', '12', '12121212', '<p>呈现演示是教师运用多种手段创设情境与环境、介绍新的目的语语言材料、指导 学生理解和掌握知识、传递教学信息的行为方式。请简述小学英语课堂中呈现演示的原 则有哪些？</p>', '', '2017年江西省教师招聘考试《小学英语》题第47题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2180269';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2180269', '2180269', '12', '', '1', '<p>呈现演示的原则： </p><p>（1）呈现演示的方式方法要根据教学内容、教学对象来确定； </p><p>（2）呈现语言要简明易懂，一般不使用学生未学过的词汇； </p><p>（3）要富有直观性、趣味性和启发性； </p><p>（4）尽量使用英语，必要时使用母语； </p><p>（5）把利用环境和创设情境结合起来。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2180269';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2180269', ',2180269', '2180269', '<p>呈现演示的原则： </p><p>（1）呈现演示的方式方法要根据教学内容、教学对象来确定； </p><p>（2）呈现语言要简明易懂，一般不使用学生未学过的词汇； </p><p>（3）要富有直观性、趣味性和启发性； </p><p>（4）尽量使用英语，必要时使用母语； </p><p>（5）把利用环境和创设情境结合起来。</p>');
