INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2180392', '12', '12121212', '<p>Three passions, simple but overwhelming strong, have governed my life：the longing for love, the search for knowledge, and unbearable pity for the suffering of mankind. These passions, like great winds, have blown me hither and thither, in a wayward course, over a deep ocean of anguish, reaching to the very verge of despair. <br></p>', '', '2016年福建省教师招聘考试《小学英语》题（专业知识）第42题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2180392';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2180392', '2180392', '12', '', '1', '<p>我的一生被三种简单却又无比强烈的激情所控制：对爱情的渴望、对知识的探求和对人类苦难不可遏制的同情心。这三种激情像狂风一样，恣意地把我吹来吹去，掠过痛苦的深海，把我吹到濒临绝望的边缘。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2180392';
