INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2232300', '2232300', '12', '', '1', '<p>高原期是指在技能形成的中、后期，练习的进步常常出现停滞，不再继续上升而保持在一定水平，有时甚至稍有下降的现象。</p>', '0');
