INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2235051', '12', '70707070', '<p>简述影响创造性的因素。</p>', '', '2017年江西省南昌市红谷滩新区教师招聘考试《教育综合知识》题第61题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2235051';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2235051', '2235051', '12', '', '1', '<p>（1）环境：家庭和学校的教育环境是影响个体创造性的重要因素。父母的受教育程度、管教方式以及家庭气氛等都在不同程度上影响孩子的创造性。教师采用民主的管理方式，鼓励学生的自主性，容许其有不同意见，给予学习活动较多的自由，容许学生自行探索，有利于学生创造性的培养。</p><p>（2）智力：创造性与智力的关系并非简单的线性关系，二者既有独立性，又在某种条件下具有相关性，在整体上呈正相关趋势。其基本关系表现为：低智商不可能具有高创造性；高智商可能有高创造性，也可能有低创造性，高创造性者必须有高于一般水平的智商。</p><p>（3）个性：创造性和个性二者之间具有互为因果的关系，一般高创造性者的个性特征为：</p><p>①具有幽默感；</p><p>②具有抱负和强烈的动机；</p><p>③能够容忍模糊与错误；</p><p>④喜欢幻想；</p><p>⑤具有强烈的好奇心。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2235051';
