INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2235670', '2235670', '12', '', '1', '<p>教师职业道德是教师在从事教育劳动时所应遵循的行为规范和必备的品德的总和，是调节教师与他人、教师与社会等关系时所必须遵守的基本道德规范和行为准则，以及在此基础上所表现出来的道德观念、情操和品质。</p>', '0');
