INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2235671', '2235671', '12', '', '1', '<p>教学过程是教师根据一定社会的要求和学生身心发展的特点，通过有目的、有计划地指导学生掌握系统的科学文化知识和基本技能，发展学生的智力和体力，培养学生的良好品德和健康个性，使其形成科学世界观的过程。</p>', '0');
