INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2235673', '12', '70707070', '<p>皮亚杰理论对小学教育的启示有哪些？</p>', '', '2017年江苏省徐州市贾汪区教师招聘考试《教育综合知识》题第44题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2235673';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2235673', '2235673', '12', '', '1', '<p>（1）根据皮亚杰的认知发展阶段理论，小学儿童正处于具体运算阶段。这个时期是儿童的逻辑思维初步发展的阶段，对于儿童的思维发展有着极其重要的意义。因此，在小学教育中要重视对儿童思维能力的培养。在教学中不能一味地追求知识的量，更要重视儿童思维活动的质。我们在教学生学习一些知识的同时，更要重视观察、引导和培养他们的思维过程。</p><p>（2）小学儿童的思维活动具有很大的具体形象性，一般还离不开具体事物的支持。因此，在教学中，我们应尽可能地使用一些图片、模型等教具来帮助学生理解相应的知识，即使在条件不允许的情况下，也应该使用各种办法帮助学生形成有助于思维的表象。到了小学高年级阶段，可以适当地减少一些教具的使用，多使用语言引导学生进行想象，适当增加一些理论、定理等抽象内容，帮助他们的思维活动逐渐摆脱对具体事物的依赖。</p><p>（3）皮亚杰十分强调活动在思维发展中的重要性，认为儿童只有参与了某种活动，才能获得真实的知识。因此，在小学教学活动中，我们还要尽量多给学生一些自己发现问题、解决问题的机会，使他们在自主学习和探索活动中掌握知识。同时，还要注意培养学生的学习兴趣和动机，增加他们学习活动的自发性和自主性。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2235673';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2235673', ',2235673', '2235673', '<p>（1）根据皮亚杰的认知发展阶段理论，小学儿童正处于具体运算阶段。这个时期是儿童的逻辑思维初步发展的阶段，对于儿童的思维发展有着极其重要的意义。因此，在小学教育中要重视对儿童思维能力的培养。在教学中不能一味地追求知识的量，更要重视儿童思维活动的质。我们在教学生学习一些知识的同时，更要重视观察、引导和培养他们的思维过程。</p><p>（2）小学儿童的思维活动具有很大的具体形象性，一般还离不开具体事物的支持。因此，在教学中，我们应尽可能地使用一些图片、模型等教具来帮助学生理解相应的知识，即使在条件不允许的情况下，也应该使用各种办法帮助学生形成有助于思维的表象。到了小学高年级阶段，可以适当地减少一些教具的使用，多使用语言引导学生进行想象，适当增加一些理论、定理等抽象内容，帮助他们的思维活动逐渐摆脱对具体事物的依赖。</p><p>（3）皮亚杰十分强调活动在思维发展中的重要性，认为儿童只有参与了某种活动，才能获得真实的知识。因此，在小学教学活动中，我们还要尽量多给学生一些自己发现问题、解决问题的机会，使他们在自主学习和探索活动中掌握知识。同时，还要注意培养学生的学习兴趣和动机，增加他们学习活动的自发性和自主性。</p>');
