INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('224276429470', '12', '50505050', '<p>分析下面的谱例。</p><p>要求：</p>', '', '2016年下半年教师资格证考试《初中音乐》题第32题', '1', '0');
