INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2242764', '224276429470', '12', '<p>（1）写出歌曲的调式调性。</p>', '1', '<p>（1）E羽雅乐七声调式</p>', '3');
DELETE FROM edu_course_exercise_qa WHERE id='2242764910';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2242764910', '224276429470', '12', '<p>（2）说明歌曲旋律音调上的特点。</p>', '1', '<p>（2）特点：①歌曲采用变换拍子，整体运用4/4拍比较舒缓的节奏，中间有一小节的节拍变换成2/4拍，使得当时的重音发生了变化，深刻地表现出梅花高雅的品质；②歌曲整体运用了二度、三度及五度的旋律进行，迂回婉转。</p>', '3');
DELETE FROM edu_course_exercise_qa WHERE id='2242764910364';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2242764910364', '224276429470', '12', '<p>（4）说明乐句间主要的结构特点。 </p><p><img width="519px" height="562px" src="https://fb.fbstatic.cn/api/tarzan/images/16588c61263dde5.png?width=700" /></p>', '0', '<p>（4）特点：前四个乐句间采用了变化重复的写作手法，<img flag="tex" src="https://fb.fbstatic.cn/api/planet/accessories/formulas?latex=g8TtwqIgdGcNILaq8wjMmQ%3D%3D" />是a的变化重复，<img flag="tex" src="https://fb.fbstatic.cn/api/planet/accessories/formulas?latex=uKIba%2BZiESkgYTN05h1Dpg%3D%3D" />是b的变化重复。</p>', '3');
