INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2242926', '224292674189', '12', '<p>（1）判断调式调性。</p>', '1', '<p>（1）乐曲开始是C自然大调，从第 3 小节开始谱例中的 F 升高了半音，且第 8 小节的和弦结束在 G 上，因此乐曲从第 3 小节开始转入 G 自然大调；从第 8 小节第二拍开始， B 降低半音，且 C 升高半音，由此可知 8 ～ 10 小节是 d 和声小调，从 11 小节开始又回到 C 自然大调。</p><p>因此，此曲的调式调性为： C 自然大调（ 1 ～ 3 小节）转入 G 自然大调（ 3 ～ 8 小节），再转入 d 和声小调（ 8 ～ 10 小节），最后回到 C 自然大调（ 11 ～ 16 小节）上。</p>', '3');
DELETE FROM edu_course_exercise_qa WHERE id='22429261';
