INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('22432243', '224322435291', '12', '<p>（2）变奏二：当节拍变换为三拍子。</p>', '0', '<p>（2）三拍子记写：</p><p><img width="509px" height="149px" src="https://fb.fbstatic.cn/api/tarzan/images/16589e4af55094c.png?width=700" /></p>', '3');
