INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2243278', '12', '50505050', '<p>为民歌《工农齐武装》片段编配第二声部。</p><p>要求：</p><p>（1）第二声部调式调性与第一声部相符。（5分）</p><p>（2）编配后的二声部为典型的模仿关系。（5分）</p><p><img width="700px" height="136px" src="https://fb.fbstatic.cn/api/tarzan/images/165f4fa62475047.png?width=700" /></p>', '', '2018年上半年教师资格证考试《初中音乐》题第31题', '1', '0');
