INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('224346875656', '224346845106', '12', '<p>（4）第1—4小节与第5—8小节之间使用了哪一种旋律发展手法? （3分）</p><p><img width="700px" height="665px" src="https://fb.fbstatic.cn/api/tarzan/images/165f52cd4eae8fd.png?width=700" /></p>', '0', '<p>（4）同头换尾的旋律发展手法。</p>', '3');
