INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('224377914053', ',224377914053', '224377914053', '<p>（4）钙在植物体内不能重复利用，即不能由老叶向嫩叶转移，故老叶片中含量较多。</p>');
