INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2244075979', '2244075', '12', '<p>（2）在将重组质粒导人大肠杆菌细胞之前，需要用_______（填物质名称）处理大肠杆菌，使之转化为能够吸收周围环境中DNA分子的__________细胞。</p>', '1', 'Ca2+；感受态', '6');
