INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2244312395', '2244312', '12', '<p>（2）此类板书有哪些优点？</p>', '1', '此板书是根据教学内容提出相应问题，让学生思考回答后书写简要的词语，形成表格。这种板书对比性强，便于比较概念的异同点，容易使学生把握概念的本质，深刻领会所学知识。这种方式易于学生找出知识的联系。类目清楚，井然有序，对比异同，培养对比分析，归纳概括等能力。', '10');
