INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2244314', '12', '40404040', '<p>《普通高中生物课程标准》（实验）中，关于“光合作用的原理和应用”的具体内容要求是“说明光合作用以及对它的认识过程”。教材主要内容有三部分： </p><p>（1）光合作用的研究历程：</p><p><img width="697px" height="573px" src="https://fb.fbstatic.cn/api/tarzan/images/16593d7571012ab.png?width=700" /></p><p>（2）光合作用的过程：光反应阶段和暗反应阶段。</p><p>（3）光合作用原理的应用。 </p><p>要求：依据上述材料，确定该部分内容的三维教学目标和教学重点。</p>', '', '2013年上半年教师资格证考试《高中生物》真题第30题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2244314';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2244314', '2244314', '12', '', '1', '<p>（1）教学目标</p><p>知识与技能</p><p>① 说明光合作用以及对它的认识过程。</p><p>② 说出光合作用原理的应用。</p><p>③ 描述光反应、暗反应过程的物质变化和能量转化。</p><p>过程与方法：尝试探究影响光合作用强度的环境因素。</p><p>情感态度与价值观：通过研究科学家对光合作用原理的探究过程，认同科学是在不断的观察、实验、探索和争论中前进的；认同科学家不仅要继承前人的科研成果，而且要善于吸收不同意见中的合理成分，还要有质疑、创新、勇于实践的科学精神和态度。</p><p>（2）教学重点</p><p>①光合作用的发现及研究历史。</p><p>②光合作用的光反应ⅱ暗反应过程及相互关系。</p>', '0');
