INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2244314', ',2244314', '2244314', '<p>（1）教学目标</p><p>知识与技能</p><p>① 说明光合作用以及对它的认识过程。</p><p>② 说出光合作用原理的应用。</p><p>③ 描述光反应、暗反应过程的物质变化和能量转化。</p><p>过程与方法：尝试探究影响光合作用强度的环境因素。</p><p>情感态度与价值观：通过研究科学家对光合作用原理的探究过程，认同科学是在不断的观察、实验、探索和争论中前进的；认同科学家不仅要继承前人的科研成果，而且要善于吸收不同意见中的合理成分，还要有质疑、创新、勇于实践的科学精神和态度。</p><p>（2）教学重点</p><p>①光合作用的发现及研究历史。</p><p>②光合作用的光反应ⅱ暗反应过程及相互关系。</p>');
