INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('224438043582', '12', '40404040', '<p>下图中甲表示洋葱根尖的不同区域，乙表示洋葱根尖处于有丝分裂各阶段细胞核中DNA和细胞质中mRNA的含量变化。</p><p><img width="493px" height="221px" src="https://fb.fbstatic.cn/api/tarzan/images/1659403c3cdca04.png?width=700" /></p><p>据图回答（1）~（3）题。</p>', '', '2012年下半年教师资格证考试《高中生物》真题第26题', '1', '0');
