INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2244380', '224438043582', '12', '<p>（1）图甲中①②③④各区细胞形态不同的原因是______________。 要得到图乙结果，________（能/不能）利用图甲②区细胞为材料，原因是_____________________________。9</p>', '1', '基因的选择性表达--^--不能--^--图甲②区细胞是伸长区细胞，细胞没有分裂能力', '9');
