INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2244413', '224441364089', '12', '<p>（1）据图判断，果蝇为<u>        </u>倍体生物，每个染色体组含有<u>        </u>条染色体，其中属于常染色体的是<u>        </u>（用图中的染色体标号作答）。9</p>', '1', '二--^--4--^--II、III、IV', '9');
