INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2246641', '12', '70707070', '<p>学校开展心理健康教育的途径有哪些？</p>', '', '2018年5月27日天津市武清区、静海区教师招聘 《教育综合知识》题第58题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2246641';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2246641', '2246641', '12', '', '1', '<p>        学校心理健康教育的途径主要有：</p><p>       （1）心理辅导课程。心理辅导课程指促进学生心理健康发展的系列专题活动，包括心理知识讲座、心理训练、专题辨析、情境设计、角色扮演、游戏辅导等。</p><p>       （2）教育教学渗透。心理健康教育具有长期性和广泛性，不能仅限于心理辅导活动课程，而是应该渗透到学校的一切教育教学活动中，可以说，学校的一切工作都应当包含着深刻的心理健康教育意义。</p><p>       （3）心理咨询。按咨询的对象，心理咨询分为个别咨询和团体咨询。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2246641';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2246641', ',2246641', '2246641', '<p>        学校心理健康教育的途径主要有：</p><p>       （1）心理辅导课程。心理辅导课程指促进学生心理健康发展的系列专题活动，包括心理知识讲座、心理训练、专题辨析、情境设计、角色扮演、游戏辅导等。</p><p>       （2）教育教学渗透。心理健康教育具有长期性和广泛性，不能仅限于心理辅导活动课程，而是应该渗透到学校的一切教育教学活动中，可以说，学校的一切工作都应当包含着深刻的心理健康教育意义。</p><p>       （3）心理咨询。按咨询的对象，心理咨询分为个别咨询和团体咨询。</p>');
