INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2246938', '2246938', '12', '', '1', '<p>布鲁纳提出认识-结构教学论，其内容主要包括：</p><p>（1）课程设计应按照学科知识的基本结构进行。</p><p>（2）教学过程注重发展儿童的认知能力。</p><p>（3）教学过程是教师引导学生发现的过程。</p>', '0');
