INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2247002', '12', '70707070', '<p>新课程标准的核心理念是教育以人为本，即“一切为了每一位学生的发展”，请结合相关知识，谈谈你对该理念的理解。</p>', '', '2018年6月30日陕西省农村特岗教师招聘考试 《教育理论知识》题第61题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2247002';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2247002', '2247002', '12', '', '1', '<p>新课程改革的核心理念是“以人为本”和“以学生发展为本”。在教学中，要关注每一位学生的发展，引导学生学会学习、学会生存、学会做人，使学生在获得基础知识和基本技能的同时学会学习和形成正确价值观。</p><p>新课程背景下的学生观认为学生是发展中的人，学生是独特的人，学生是具有独立意义的人。这就要求教师在教学过程中，遵循学生的身心发展规律，把学生看作是一个完整的个体，尊重学生学习的主体性和想法，充分发挥学生的主观能动性，引导学生进行自主、合作和研究学习，才能真正做到“一切为了每一位学生的发展”。</p><p>新课改背景下的教师观认为教师是学生学习的促进者，这就要求教师充分发挥其主导作用，引导学生朝着积极健康方向发展。</p><p>新课改背景下的评价观要求教师突出学生在评价中的主体地位，采取自评、互评等方式，不以分数作为评价学生的唯一标准，采用量化评价与质性评价相结合的评价，这就要求我们教师对学生进行发展性、动态性、多元化的评价，最终促进学生全面发展。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2247002';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2247002', ',2247002', '2247002', '<p>新课程改革的核心理念是“以人为本”和“以学生发展为本”。在教学中，要关注每一位学生的发展，引导学生学会学习、学会生存、学会做人，使学生在获得基础知识和基本技能的同时学会学习和形成正确价值观。</p><p>新课程背景下的学生观认为学生是发展中的人，学生是独特的人，学生是具有独立意义的人。这就要求教师在教学过程中，遵循学生的身心发展规律，把学生看作是一个完整的个体，尊重学生学习的主体性和想法，充分发挥学生的主观能动性，引导学生进行自主、合作和研究学习，才能真正做到“一切为了每一位学生的发展”。</p><p>新课改背景下的教师观认为教师是学生学习的促进者，这就要求教师充分发挥其主导作用，引导学生朝着积极健康方向发展。</p><p>新课改背景下的评价观要求教师突出学生在评价中的主体地位，采取自评、互评等方式，不以分数作为评价学生的唯一标准，采用量化评价与质性评价相结合的评价，这就要求我们教师对学生进行发展性、动态性、多元化的评价，最终促进学生全面发展。</p>');
