INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2250036', '12', '91919191', '<p>简述英语校内课外活动的三个主要作用（6分），列出四种活动形式（8分），并写出教师组织课外活动的三点注意事项（6分）。</p>', '', '2018年上半年教师资格证考试《初中英语》题第31题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2250036';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2250036', '2250036', '12', '', '0', '<p>（1）英语校内课外活动的三个主要作用：</p><p>① 英语校内课外活动是学生英语学习的重要组成部分，能为学生的语言实践和</p><p>自主学习提供更大的平台。</p><p>② 英语校内课外活动有助于激发和提升学生学习英语的兴趣，帮助学生丰富语感、开阔视野、增长知识、发展智力和塑造性格。</p><p>③ 英语校内课外活动有助于提升学生的活动组织能力和管理能力。</p><p>（2）英语校内课外活动的四种形式：</p><p>① 英语角。英语角为广大爱好英语的学生提供了机会，能够带动更多的学生用英语进行交流， 学生把所学用到生活中去。</p><p>② 英语演讲。学校可以组织开展小组间、班级间、学校间的英语演讲比赛，鼓励学生积极参与， 提高学生的英语口语水平。</p><p>③ 英语板报、制作英语刊物。英语板报是教育学生、锻炼学生的重要方式，学校可以定期组织学生设计英语板报、制作英语刊物，以提高其学习英语的积极性。</p><p>④ 英语短剧会演、英语诗歌朗诵和英语歌曲大赛。学校可以根据具体情况，定期组织这些活动， 丰富学生的校内课外活动。</p><p>（3）教师组织课外活动的三点注意事项：</p><p>① 教师要根据学生的年龄特点和需求，考虑学生的兴趣、特长和差异，结合当地的实际情况， 因地制宜地开展英语校内课外活动。</p><p>② 教师应注意，英语校内课外活动要注重调动学生参与的积极性。要鼓励吸引有学习困难的学生参与课外活动，为他们设计合适的活动形式和内容，引导学生乐于参与，</p><p>通过亲身体验和实践产生学习兴趣和动力并获得成功。在活动中，老师要给予他们耐心</p><p>的指导，注意保护他们的自尊心和热情，尊重他们的参与方式，并注意因势利导。</p><p>③ 活动内容要丰富多彩，组织形式要多样化，要富有吸引力，同时，教师要充分发挥学生的自主性，尊重他们的策划和选择，并给予恰当的引导和辅导，要关注活动的过程，关注学生在活动中的表现，做好活动的顾问和服务工作，为他们的发展提供指导和支持。</p>', '0');
