INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2250036', ',2250036', '2250036', '<p>本题考查英语校内外活动的三个主要作用、活动形式及注意事项。考生只需分条列出相关知识点即可。</p>');
