INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2258122', '12', '41414141', '<p>简述家校合作的途径。</p>', '', '2018年下半年教师资格证考试《教育教学知识与能力》（小学）题第22题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2258122';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2258122', '2258122', '12', '', '1', '<p>一般而言，家校联系的常用方式主要包括家访、班级家长会、家长学校、家长沙龙和家长委员会。</p><p>（1）家访。这是由学校的教师和干部到学生家庭进行访问，一般是与家长沟通情况、交流感情、密切关系，商讨共同教育儿童、青少年的方式方法。</p><p>（2）班级家长会。这是一种传统的家校合作方式，其主要目的是使家长与班主任及学科教师直接面对面地集中沟通，交流意见或建议，增进互信理解与支持，共同为学生进一步发展协调配合。</p><p>（3）家长学校。家长学校是组织学生家长学习进修的教育机构。家长在专业教师的引领指导下，学习教育学、心理学方面的知识，以及教育子女的方法，由此，能更好地配合班主任教育孩子，做好班级管理工作。</p><p>（4）家长沙龙和家长委员会。家长沙龙是以家长为主体，以学生学习成长为中心，以教师及专家学者为咨询指导，旨在提高家长教育素养，提升教育理念，转变传统教育观念，实现以家庭教育为突破口，最终形成教育合力的一种形式。家长委员会由关心学校、关心教育事业、具有教育子女经验的家长代表组成，其主要职责是参与学校和班级的教育与管理，协助做好学生教育工作。</p><p>（5）班级网络</p><p>教育技术现代化是当今教育的一个世界性趋势，是推进学习型家庭建设的合作互动策略。网络技术的普及推动了班级网络成为家庭与学校沟通的新载体。新的社会形势下，成人的竞争压力日趋激烈，缺乏足够的时间与学校老师进行交流，班级网络的出现为家长提供了一个非常有利的平台。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2258122';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2258122', ',2258122', '2258122', '<p>一般而言，家校联系的常用方式主要包括家访、班级家长会、家长学校、家长沙龙和家长委员会。</p><p>（1）家访。这是由学校的教师和干部到学生家庭进行访问，一般是与家长沟通情况、交流感情、密切关系，商讨共同教育儿童、青少年的方式方法。</p><p>（2）班级家长会。这是一种传统的家校合作方式，其主要目的是使家长与班主任及学科教师直接面对面地集中沟通，交流意见或建议，增进互信理解与支持，共同为学生进一步发展协调配合。</p><p>（3）家长学校。家长学校是组织学生家长学习进修的教育机构。家长在专业教师的引领指导下，学习教育学、心理学方面的知识，以及教育子女的方法，由此，能更好地配合班主任教育孩子，做好班级管理工作。</p><p>（4）家长沙龙和家长委员会。家长沙龙是以家长为主体，以学生学习成长为中心，以教师及专家学者为咨询指导，旨在提高家长教育素养，提升教育理念，转变传统教育观念，实现以家庭教育为突破口，最终形成教育合力的一种形式。家长委员会由关心学校、关心教育事业、具有教育子女经验的家长代表组成，其主要职责是参与学校和班级的教育与管理，协助做好学生教育工作。</p><p>（5）班级网络</p><p>教育技术现代化是当今教育的一个世界性趋势，是推进学习型家庭建设的合作互动策略。网络技术的普及推动了班级网络成为家庭与学校沟通的新载体。新的社会形势下，成人的竞争压力日趋激烈，缺乏足够的时间与学校老师进行交流，班级网络的出现为家长提供了一个非常有利的平台。</p>');
